import React, { useRef, useState } from 'react';
function DraggableApplicationBorder({ children, source}) {
    const elementRef = useRef(null);

    const handleMouseDown = (event) => {
        const element = elementRef.current;
        const startWidth = element.width;
        const startHeight = element.height;

        const handleMouseMove = (moveEvent) => {
            document.body.style.cursor = 'move';
            element.style.width = `${moveEvent.pageX - startWidth}px`;
            element.style.height = `${moveEvent.pageY - startHeight}px`;
        };

        document.addEventListener('mousemove', handleMouseMove);

        document.addEventListener('mouseup', () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.body.style.cursor = 'auto';
      }, { once: true });
    };

    return (
      <div
        ref={elementRef}
        onMouseDown={handleMouseDown}
        onTouchMove={handleMouseDown}
        className='DraggableApplicationBorder'
        style={{ position: 'absolute', border:'10px', borderColor:'green', borderStyle:'solid'}}
      >
        {children}
      </div>
    );
  }

export default DraggableApplicationBorder;
