import React, { useRef } from 'react';
import AppsButton from './AppsButton.jsx';
import Image from './assets/LinkedIn.png';
import Image1 from './assets/GitHub.png';
function DraggableComponent({ children }) {
  const elementRef = useRef(null);

  const handleMouseDown = (event) => {
    const element = elementRef.current;
    const startX = event.pageX - element.offsetLeft;
    const startY = event.pageY - element.offsetTop;

    const handleMouseMove = (moveEvent) => {
      element.style.left = `${moveEvent.pageX - startX}px`;
      element.style.top = `${moveEvent.pageY - startY}px`;
    };

    document.addEventListener('mousemove', handleMouseMove);

    document.addEventListener('mouseup', () => {
      document.removeEventListener('mousemove', handleMouseMove);
    }, { once: true });
  };

  return (
    <div
      ref={elementRef}
      onMouseDown={handleMouseDown}
      style={{ position: 'absolute', cursor: 'move', left: '6vw', top: '4vh'}}
    >
      {children}
    </div>
  );
}

function Application() {
  return (
      <>
      <DraggableComponent className="DraggableOne">
        <AppsButton title='LinkedIn' source={Image} alternative='LinkedIn logo'/>
      </DraggableComponent>
      <DraggableComponent className="DraggableOne">
        <AppsButton title='GitHub' source={Image1}  alternative='GitHub logo'/>
      </DraggableComponent>
      </>

  );
}

export default Application;
