import DraggableApplication from "./DraggableApplication"
import ApplicationTopBar from "./ApplicationTopBar"
import React, { useRef, useState } from 'react';
function RunningApplication({}) {  
    const aboutMe = {
      title:"About Me",
      contents:"about me..."
    };
    const education = {
      title:"Education",
      contents:"about me..."
    };
    const [title,changeTitle] = useState(aboutMe["title"]);
    const [content,changeContent] = useState(aboutMe["contents"]);

    const educationFunction = event => {
      changeTitle(education["title"]);
      changeContent(education["contents"])
    }

    const aboutMeFunction = event => {
      changeTitle(aboutMe["title"]);
      changeContent(aboutMe["contents"])
      const box = document.getElementById('active');
      box.removeAttribute('active');
      let element = event.currentTarget;
      element.classList.add('ApplicationOptionsOptionActive');
    }

    return (
      <>

        <DraggableApplication>
        <ApplicationTopBar/>
        <div className="RunningApplication">
            <div className='applicationOptions'>
              <div className="ApplicationOptionsOption" onClick={aboutMeFunction} id="active">
                <h5>About Me</h5>
              </div>
              <div className="ApplicationOptionsOption" onClick={educationFunction}><h5>Education</h5></div>
              <div className="ApplicationOptionsOption"><h5>Volunteering</h5></div>
              <div className="ApplicationOptionsOption"><h5>Contact Me</h5></div>

              </div>
            <div><h3>{title}</h3>
            <p>{content}</p></div>

        </div>
        </DraggableApplication>
      </>
    )
  }

  export default RunningApplication
