import './App.css';
import Application from './Application.jsx'
import TopBar from './TopBar.jsx';
import TaskBar from './TaskBar.jsx';
import React, { useState } from 'react';
import RunningApplication from './RunningApplication.jsx';


function App() {
  const [isVisible, setIsVisible] = useState(true);

  const toggleVisibility = () => {
    setIsVisible(!isVisible);
    var w = document.documentElement.clientWidth || window.innerWidth;
    console.log(w)
  };

  const isVisibleFunction = () =>{
    return isVisible;
  }

  return (
    <div className='App'>
      <TaskBar toggleVisibility={toggleVisibility}/>
      <TopBar/>
      <Application toggleVisibility={toggleVisibility}/>
      {isVisible && (
        <RunningApplication toggleVisibility={toggleVisibility} loading="lazy"/>
      )}

    </div>
  );
}

export default App;
